INSERT INTO `about_pages` (`id`, `title`, `description`, `keywords`, `headline`, `short_desc`, `image`, `alt`, `main_image`, `body`, `location`, `class`, `sub_class`, `created_at`, `updated_at`) VALUES
(1, 'Beita', 'Beita', 'Beita', 'Beita', 'Hágæða beita sem notuð er til veiða í Norður-Atlantshafi fyrir allar stærðir af línubátum.\r\n', 'riba.png', 'Beita', 'brod.png', '<h6 class=\'font-serif font-bold text-6xl text-black\'>Beita</h6>\r\n<p class=\'mt-6\'>\r\nHágæða beita sem notuð er til veiða í Norður-Atlantshafi fyrir allar stærðir af línubátum. \r\n</p>\r\n<p class=\'mt-6\'>\r\nBeita er ekki bara beita! Við bjóðum hágæða beitu frá traustum birgjum fyrir veiðar í Norður-Atlantshafi. Við verslum eingöngu við fyrirtæki sem bjóða áreiðanlega og góða vöru og höfum byggt upp traust og gott samband við alla okkar birgja. \r\n</p>\r\n<p class=\'mt-6\'>\r\nVið erum með fjórar tegundir af beitu; saury, síld, makríl og smokkfiskur, og þær eru allar fáanlegar í ýmsum stærðum. Við afgreiðum beituna sama dag og þú pantar og sendum hana hvert sem er í heiminum. Pantaðu hér og við sendum þér beituna um hæl, hvert sem er í heiminum.\r\n</p>\r\n', 'beita', 'beitaa', 'beita-sub', NULL, NULL),
(2, 'Veiðarfæri', 'Veiðarfæri', 'Veiðarfæri', 'Veiðarfæri', 'Slitþolnar og endingagóðar línur gerðar fyrir íslenskar aðstæður!', 'hli-cat.png', 'Veiðarfæri', 'vei1.png', '<h6 class=\'font-serif font-bold text-6xl text-black\'>Veiðarfæri</h6>\r\n<p class=\'mt-6\'>\r\nSlitþolnar og endingagóðar línur gerðar fyrir íslenskar aðstæður! \r\n</p>\r\n<p class=\'mt-6\'>\r\nVið bjóðum upp á vandaðar vörur til línuveiða fyrir allar stærðir línubáta. Línurnar okkar eru mjög slitþolnar og endingagóðar og eru fáanlegar í fimm stærðum; 6mm, 7.2mm, 9.5mm, 11.5mm og 12mm í þvermál. Þú getur valið um að fá línuna bæði uppsetta og óuppsetta. Það er mjög ánægjulegt að segja frá því að línurnar okkar komu best út í prófi sem óháður aðili framkvæmdi á slitþoli lína frá þremur ólíkum framleiðendum. Þá erum við einnig með króka, gogga og allt annað sem þarf til línuveiða. \r\n</p>\r\n<p class=\'mt-6\'>\r\nPantaðu hágæða línu, króka, gogga eða það sem þig vantar hér og við sendum hana hvert á land sem er.\r\n</p>\r\n', 'veidarferi', 'veidarferii\r\n\r\n', 'veidarferi-sub', NULL, NULL),
(3, 'Hlífðarfatnaður', 'Hlífðarfatnaður', 'Hlífðarfatnaður', 'Öryggis-og vinnufatnaður', 'Vandaður öryggisfatnaður hannaður samkvæmt ströngustu kröfum um öryggi!', 'contact-section.png', 'Öryggis-og vinnufatnaður', 'orrr.png', '<h6 class=\'font-serif font-bold text-6xl text-black\'>Öryggis-og vinnufatnaður</h6>\r\n<p class=\'mt-6\'>\r\nVandaður öryggisfatnaður hannaður samkvæmt ströngustu kröfum um öryggi! \r\n</p>\r\n<p class=\'mt-6\'>\r\nHjá okkur færðu vandaðan öryggisfatnað sem við framleiðum undir okkar eigin vörumerki - Mar Wear - sem er hannaður samkvæmt ströngustu kröfum um öryggi. Við hönnun Mar Wear tökum við sérstaklega tillit til þeirra erfiðu og krefjandi aðstæðna sem oft finnast hér við land.\r\n</p>\r\n<p class=\'mt-6\'>\r\n Við bjóðum gríðarlega mikið úrval af alls kyns hlífðar- og öryggisfatnaði sem er sniðinn fyrir þínar þarfir. Þú færð hanska, stígvél, vinnslu- og sjófatnað, öryggis- og vinnuskó og flotgalla hjá okkur, ásamt öllu öðru sem þig gæti vantað í vinnsluna eða um borð. Ef við eigum það ekki til, þá finnum við það! Allar pantanir afgreiddar samdægurs. Pantaðu hér og við sendum.\r\n</p>', 'hlifdarfatnadur', 'hlifdarfatnadurr', 'hlifdarfatnadur-sub', NULL, NULL),
(4, 'Rekstrarvörur', 'Rekstrarvörur', 'Rekstrarvörur', 'Rekstrarvörur', 'Fjölbreytt úrval af þaulreyndri rekstrarvöru á hagstæðu verði sem auðveldar þér lífið!', 'noz.png', 'Rekstrarvörur', 'rekss.png', '<h6 class=\'font-bold font-serif text-6xl text-black\'>Rekstrarvörur</h6>\r\n<p class=\'mt-6\'>\r\nFjölbreytt úrval af þaulreyndri rekstrarvöru á hagstæðu verði sem auðveldar þér lífið! \r\n</p>\r\n<p class=\'mt-6\'>\r\nHjá Voot færð þú mikið úrval af þaulreyndri rekstrarvöru á hagstæðu verði sem auðveldar þér reksturinn og þjónustu við viðskiptavini. Við aðstoðum þig við að meta þarfirnar, hvort sem þú ert í sjávarútvegi eða tengdum rekstri, og veitum faglega og persónulega þjónustu. Hjá okkur færðu mikið úrval af hreinsiefni, eldhúsvörum, salernisvörum og ásamt öðru ræstiefni. Við kappkostum að bjóða upp á hagkvæmar lausnir sem henta þér. Ekki hika við að hafa samband við okkur og við ráðleggjum hvað hentar í hverju verkefni. \r\n</p>\r\n<p class=\'mt-6\'>\r\nVið afgreiðum allar pantanir samdægurs og sendum um heim allan.\r\n</p>', 'rekstrarvorur', 'rekstrarvorurr', 'rekstrarvorur-sub', NULL, NULL);

